mapper.delete:
delete from zs_account 	
WHERE ID IN
 #{item}

update zs_account set status1 = '-1' 	
WHERE ID IN
 #{item}

mapper.update:
update zs_account
CREATE_BY=#{createBy}, CREATE_TIME=#{createTime}, UPDATE_BY=#{updateBy}, UPDATE_TIME=#{updateTime}, BID=#{bid}, ACCOUNT=#{account}, PASSWORD=#{password}, LAST_TIME=#{lastTime}, STATE=#{state}, TOKEN=#{token}, TOKEN_TIME=#{tokenTime}, REMARK=#{remark}, PARAM1=#{param1}, PARAM2=#{param2}, PARAM3=#{param3}, PARAM4=#{param4}, PARAM5=#{param5}
where ID=#{id}

mapper.select:
select a.ID id, a.CREATE_BY createBy, a.CREATE_TIME createTime, a.UPDATE_BY updateBy, a.UPDATE_TIME updateTime, a.BID bid, a.ACCOUNT account, a.PASSWORD password, a.LAST_TIME lastTime, a.STATE state, a.TOKEN token, a.TOKEN_TIME tokenTime, a.REMARK remark, a.PARAM1 param1, a.PARAM2 param2, a.PARAM3 param3, a.PARAM4 param4, a.PARAM5 param5 from zs_account a
AND ( 1=2
OR a.id like #{item}
)
